// Declaration file extracted form an older Snowpack project.

/* CSS & CSS Modules */
declare module "*.css";
declare module "*.scss";

/* IMAGES */
declare module "*.svg" {
  const ref: string;
  export default ref;
}
declare module "*.bmp" {
  const ref: string;
  export default ref;
}
declare module "*.gif" {
  const ref: string;
  export default ref;
}
declare module "*.jpg" {
  const ref: string;
  export default ref;
}
declare module "*.jpeg" {
  const ref: string;
  export default ref;
}
declare module "*.png" {
  const ref: string;
  export default ref;
}
